import React, { useState } from 'react';
import { Grid, Card, CardContent, Typography, Select, MenuItem } from '@mui/material';

const mockDeals = [
    { id: 1, title: 'Flight to NYC', category: 'Travel', price: 200 },
    { id: 2, title: 'Pizza Meal', category: 'Food', price: 15 },
    { id: 3, title: 'Smartphone', category: 'Hot Deals', price: 699 },
];

const DealsPage = () => {
    const [selectedCategory, setSelectedCategory] = useState('');

    const filteredDeals = selectedCategory
        ? mockDeals.filter((deal) => deal.category === selectedCategory)
        : mockDeals;



    return (
        <div style={{ padding: 20 }}>
            <Select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                displayEmpty
                style={{ marginBottom: 20 }}
            >
                <MenuItem value="">All Categories</MenuItem>
                <MenuItem value="Travel">Travel</MenuItem>
                <MenuItem value="Food">Food</MenuItem>
                <MenuItem value="Hot Deals">Hot Deals</MenuItem>
            </Select>
            <Grid container spacing={3}>
                {filteredDeals.map((deal) => (
                    <Grid item xs={12} sm={6} md={4} key={deal.id}>
                        <Card>
                            <CardContent>
                                <Typography variant="h6">{deal.title}</Typography>
                                <Typography color="textSecondary">{deal.category}</Typography>
                                <Typography variant="h5">${deal.price}</Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </div>
    );
};

export default DealsPage;
