import React, { useState } from 'react';
import {
    Grid,
    Card,
    CardContent,
    Typography,
    Select,
    MenuItem,
    Button,
    Box,
} from '@mui/material';

// Mock deals data
const mockDeals = [
    // Travel deals
    { id: 1, title: 'Flight to NYC', category: 'Travel', subcategory: 'Flight', company: 'United Airlines', price: 200 },
    { id: 2, title: 'Flight to LA', category: 'Travel', subcategory: 'Flight', company: 'Spirit Airlines', price: 180 },
    { id: 3, title: 'Bus to Boston', category: 'Travel', subcategory: 'Bus', company: 'Greyhound', price: 30 },
    { id: 4, title: 'Bus to Chicago', category: 'Travel', subcategory: 'Bus', company: 'FlixBus', price: 25 },

    // Food deals
    { id: 5, title: 'Large Pepperoni Pizza', category: 'Food', subcategory: 'Pizza', company: 'Domino\'s', price: 12 },
    { id: 6, title: 'Chicken Sandwich Combo', category: 'Food', subcategory: 'Sandwiches', company: 'Subway', price: 10 },
    { id: 7, title: 'Cheeseburger Meal', category: 'Food', subcategory: 'Burgers', company: 'Wendy\'s', price: 8 },
    { id: 8, title: 'Hawaiian Pizza', category: 'Food', subcategory: 'Pizza', company: 'Papa John\'s', price: 14 },
    { id: 9, title: 'Grilled Chicken Wrap', category: 'Food', subcategory: 'Sandwiches', company: 'Chick-fil-A', price: 9 },
    { id: 10, title: 'Beef Burger', category: 'Food', subcategory: 'Burgers', company: 'Burger King', price: 7 },

];

const DealsPage = () => {
    const [selectedCategory, setSelectedCategory] = useState('');
    const [selectedSubcategory, setSelectedSubcategory] = useState('');

    // Filter deals based on selected category and subcategory
    const filteredDeals = mockDeals.filter((deal) => {
        if (selectedCategory && deal.category !== selectedCategory) return false;
        if (selectedSubcategory && deal.subcategory !== selectedSubcategory) return false;
        return true;
    });

    // Subcategories for each main category
    const subcategories = {
        Travel: ['Flight', 'Bus'],
        Food: ['Pizza', 'Sandwiches', 'Burgers'],
    };

    return (
        <div style={{ padding: 20 }}>
            {/* Category Filter */}
            <Select
                value={selectedCategory}
                onChange={(e) => {
                    setSelectedCategory(e.target.value);
                    setSelectedSubcategory(''); // Reset subcategory on category change
                }}
                displayEmpty
                style={{ marginBottom: 20 }}
            >
                <MenuItem value="">All Categories</MenuItem>
                <MenuItem value="Travel">Travel</MenuItem>
                <MenuItem value="Food">Food</MenuItem>
            </Select>

            {/* Subcategories */}
            {selectedCategory && subcategories[selectedCategory] && (
                <Box display="flex" gap={2} marginBottom={2}>
                    {subcategories[selectedCategory].map((subcategory) => (
                        <Button
                            key={subcategory}
                            variant={selectedSubcategory === subcategory ? 'contained' : 'outlined'}
                            onClick={() => setSelectedSubcategory(subcategory)}
                        >
                            {subcategory}
                        </Button>
                    ))}
                </Box>
            )}

            {/* Display Deals */}
            <Grid container spacing={3}>
                {filteredDeals.map((deal) => (
                    <Grid item xs={12} sm={6} md={4} key={deal.id}>
                        <Card>
                            <CardContent>
                                <Typography variant="h6">{deal.title}</Typography>
                                <Typography color="textSecondary">{deal.company}</Typography>
                                <Typography color="textSecondary">{deal.subcategory}</Typography>
                                <Typography variant="h5">${deal.price}</Typography>
                            </CardContent>
                        </Card>
                    </Grid>
                ))}
            </Grid>
        </div>
    );
};

export default DealsPage;
